def get_theme(text):
    text = text.lower()  # Normalize to lowercase
    theme_scores = {}

    for theme, keywords in theme_keywords.items():
        count = sum(1 for word in keywords if word in text)
        theme_scores[theme] = count

    # Find theme with the highest score
    max_score = max(theme_scores.values())
    if max_score == 0:
        return "No relevant theme found"

    # You could also return a list of all themes with the max score if needed
    best_theme = max(theme_scores, key=theme_scores.get)
    return best_theme


